mk_circle <- function(lat, lon, color) {
    list(type='Feature',
         properties=list(color='black', fillColor=mnsl(sprintf("5PB %d/10", color), fix=TRUE)),
         geometry=list(type='Point', coordinates=c(lon, lat)))
}


